package datagram.player;import javax.sound.sampled.AudioInputStream;import javax.sound.sampled.AudioSystem;import javax.sound.sampled.Clip;import java.io.File;import java.net.URL;public class ThreadPlayer extends Thread implements Runnable {    private File fileIn;    public ThreadPlayer (File file){        this.fileIn=file;    }    @Override    public void run() {        try {            Clip clip = AudioSystem.getClip();            AudioInputStream audioInputStream = AudioSystem.getAudioInputStream(fileIn);            clip.open(audioInputStream);            clip.start();            while(clip.isActive()){                System.out.println(clip.isActive());                if (!clip.isActive())                    break;            }            clip.stop();            clip.close();            audioInputStream.close();        } catch (Exception e) {            System.err.println(e.getMessage());        }    }}